var mail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;	
function clear_error(upto){
	var ids = ['u_error','e_error','ve_error','d_error','dob_error','c_error','p_error','cp_error','pdm_error'];		
	for(i=0;i<upto;i++){
		$("#"+ids[i]).removeClass('tt_show');		
	}
}
function validate_login(){	
	var uname = document.getElementById("username").value;
	var pass = document.getElementById("password").value;
	
	if(!uname){		
		$("#lu_error").addClass('tt_show');
	}
	else if(!pass){
		$("#lp_error").addClass('tt_show');
		$("#lu_error").removeClass('tt_show');
	}
	else{
		$("#lu_error").removeClass('tt_show');
		$("#lp_error").removeClass('tt_show');
		document.getElementById("login_form").submit();
	}
}
function validate_creds(){	
	var pass = document.getElementById("password").value;
	var cpass = document.getElementById("cpassword").value;
	if(!pass){
		$("#p_error").addClass('tt_show');
		clear_error(6);
	}
	else if(!cpass){
		$("#cp_error").addClass('tt_show');
		clear_error(7);
	}
	else if(pass!=cpass){
		$("#pdm_error").addClass('tt_show');
		clear_error(8);
	}
	else{		
		clear_error(9);
		document.getElementById("creds_form").submit();
	}
}
function validate_register(){	
	var uname = document.getElementById("rusername").value;
	var email = document.getElementById("email").value;
	var pass = document.getElementById("rpassword").value;
	var cpass = document.getElementById("rcpassword").value;
	var dob = document.getElementById("dob").value;
	var city = document.getElementById("city").value;
	
	if(!uname){
		$("#u_error").addClass('tt_show');
	}
	else if(!email){
		$("#e_error").addClass('tt_show');
		clear_error(1);
	}
	else if(!mail.test(email)){
		$("#ve_error").addClass('tt_show');
		clear_error(2);
	}
	else if(!dob){
		$("#d_error").addClass('tt_show');
		clear_error(3);
	}
	else if(dob.length!=10){
		$("#dob_error").addClass('tt_show');
		clear_error(4);
	}
	else if(!city){
		$("#c_error").addClass('tt_show');
		clear_error(5);
	}	
	else if(!pass){
		$("#p_error").addClass('tt_show');
		clear_error(6);
	}
	else if(!cpass){
		$("#cp_error").addClass('tt_show');
		clear_error(7);
	}
	else if(pass!=cpass){
		$("#pdm_error").addClass('tt_show');
		clear_error(8);
	}
	else{		
		clear_error(9);
		document.getElementById("register_form").submit();
	}
}
function enable_me(this_element){
	document.getElementById(this_element).removeAttribute("readonly");
}